function filterStudents(e){var t=document.getElementById("selectable-students").getElementsByTagName("li"),l=document.getElementById("selected-students").getElementsByTagName("li");if(""!=e){e=e.toLowerCase();for(n=0;n<t.length;n++){t[n].innerHTML.toLowerCase().indexOf(e)>-1?t[n].style.display="":t[n].style.display="none"}for(n=0;n<l.length;n++){l[n].innerHTML.toLowerCase().indexOf(e)>-1?l[n].style.display="":l[n].style.display="none"}}else{for(var n=0;n<t.length;n++)t[n].style.display="";for(var n=0;n<l.length;n++)l[n].style.display=""}}